import styled from "styled-components";
export const Button = styled.button`
  color: palevioletred;
  font-size: 1em;
  padding: 0.25em 1em;
  border: 2px solid palevioletred;
  border-radius: 3px;
  margin-right: 10px;
  cursor: pointer;
`;
